import React from "react";

export default class Chat extends React.Component {
	constructor() {
		super();
		this.state = {
			enableMessageInput: false,
		};
	}

	render() {
		return (
			<div class="container">
				<h1>RPS Chat</h1>
				{!this.state.enableMessageInput ? (
					<form class="name-form">
						<input type="text" name="nickname" placeholder="Choose Nickname" required />
						<button onSubmit={handleNameSubmit}>Enter Chat</button>
					</form>
				) : (
					<div class="chatroom hidden">
						<ul class="chat-list"></ul>
						<form class="chat-form">
							<input type="text" name="mssg" placeholder="Type message..." required />
							<button>Send</button>
						</form>
					</div>
				)}
			</div>
		);
	}
}
