import React from "react";

export default class Chat extends React.Component {
	constructor() {
		super();
		this.state = {
			enableMessageInput: false,
			name: "",
			message: "",
			messages: "",
			data: undefined,
		};
		let ws = new WebSocket("ws://localhost:3000/ws");
	}

	componentDidMount() {
		this.ws.addEventListener("message", this.addMessage);
	}

	handleNameSubmit = (e) => {
		e.preventDefault();
		this.setState({ enableMessageInput: true });
	};

	handleMessageSubmit = (e) => {
		e.preventDefault();
		// this.ws.send(JSON.stringify({ name: this.state.name, message: this.state.message }));
	};

	addMessage = ({ data }) => {
		this.setState({ data });
	};

	getChats = () => {
		// const { name, mssg } = JSON.parse(this.state.data);
		console.log(this.state.data);

		// return (
		// 	<li>
		// 		<div class="name">${name}</div>
		// 		<div class="mssg">${mssg}</div>
		// 	</li>
		// );
	};

	render() {
		return (
			<div className="container">
				<h1>RPS Chat</h1>
				{!this.state.enableMessageInput ? (
					<form onSubmit={this.handleNameSubmit}>
						<input
							type="text"
							value={this.state.name}
							onChange={(e) => this.setState({ name: e.target.value })}
							placeholder="Choose a name"
							//  required
						/>
						<input type="submit" value="Submit"></input>
					</form>
				) : (
					<div>
						{this.getChats()}
						<form onSubmit={this.handleMessageSubmit}>
							<input
								type="text"
								value={this.state.message}
								onChange={(e) => this.setState({ message: e.target.value })}
								placeholder="Enter a chat"
								required
							/>
							<input type="submit" value="Submit"></input>
						</form>
					</div>
				)}
			</div>
		);
	}
}
